import React, { Component } from 'react'
import { Paper, List, ListItem, ListItemText } from '@mui/material'


export class MessageList extends Component {
    render() {
        const messages = this.props.events? this.props.events.map( (message) => {
            var text
            if(!message.created) debugger
            var dateAndTime = new Date(message.created).toLocaleDateString("en-US") + ' at ' + new Date(message.created).toLocaleTimeString("en-US")
            switch(message.type){
                case 'Message':
                    text = message.user? `${message.user} says: ${message.message} at ${dateAndTime}` : `${message.message} at ${dateAndTime}`
                    break;
                case 'Join':
                    text = `JOIN ${message.user} at ${dateAndTime}`
                    break;
                case 'Part':
                    text = `PART ${message.user} at ${dateAndTime}`
                    break;
                case 'Status':
                    text= `${message.message} at ${dateAndTime}`
                    break;
                default:
                    debugger
                    return
            }
            return( <ListItem key={message.created.toString()} disablePadding>
                <ListItemText primary={text} />
            </ListItem>)
        }) : 'no messages'
        return (
            <Paper style={{ paddingLeft: '8px', height: '85vh', overflow: 'auto'}}>
                <List style={{ display: 'flex', flexDirection: 'column-reverse', overflow: 'auto'}}>
                {messages}
                <div ref={(el) => { this.messagesEnd = el; }}></div>
                </List>
            </Paper>
        )
    }
}

export default MessageList
