import { Component } from "react";
import "./App.css";

import { Dialog, FormControl, FormGroup, FormLabel, Input } from '@mui/material';
import { Box } from "@mui/system";
import API from './helpers/client'





class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      username:"",
      password:"",
      needToLogin: true,
      messageToken: "",
      streamToken: "",
      url: "https://chat.cs291.com"
    }
    this.manageSubmit = this.manageSubmit.bind(this)
    this.serve = this.serve.bind(this)
    this.login = this.login.bind(this)
  }

    serve(){
      const server = new EventSource("http://localhost:3000/stream/a");
      server.addEventListener("message", (event) => {
        if (event.data === "Goodbye!") {
          console.log("Closing SSE connection");
          server.close();
        } else {
          console.log(event.data);
        }
      });
      server.onerror = (_event) => {
        console.log("Connection lost, reestablishing");
      };
    }



    login() {
      var request = new XMLHttpRequest();
      var form = new FormData();
      form.append("password", this.state.password);
      form.append("username", this.state.username);
      sessionStorage.setItem("url", this.state.url);
      request.open("POST", sessionStorage.getItem("url") + "/login");
      request.onreadystatechange = function() {
          if (this.readyState != 4) return;
          if (this.status === 201) {
              // login_modal.style.display = "none";
              this.setState({password: "",
              username: ""})
              const data = JSON.parse(this.responseText);
              this.state.messageToken = data.message_token;
              this.state.streamToken = data.stream_token;
              // start_stream();
          } else if (this.status === 403) {
              alert("Invalid username or password");
          } else if (this.status === 409) {
              alert(this.state.username + " is already logged in");
          } else {
              alert(this.status + " failure to /login");
          }
      };
      request.send(form);
    }



  manageSubmit = () => {
    this.login()
    // var loginFormData = new FormData();

    // loginFormData.append('username', this.state.username);
    // loginFormData.append('password', this.state.password)


    // API.post('/login', loginFormData, {headers: {"ContentType" : 'multipart/form-data'}}).then( (reply) => {
    //   console.log(reply)
    //   // debugger
    //   this.setState({needToLogin: false, username: "", password: ""})
    // }).catch( (e) => {
    //   console.log(e)
    //   // debugger
    // })
  }



  render() {
    return (
      <>
        <Dialog open={this.state.needToLogin}>
          <Box p={5}>
            <FormGroup>
              <Box pb={2}>
                <FormLabel>
                  {"Username: "}
                </FormLabel>
                <Input onChange={(e) => this.setState({username: e.target.value})} value={this.state.username}/>
              </Box>
              <Box id="loginForm">
                <FormLabel id="loginForm">
                    {"Password: "}
                  </FormLabel>
                <Input id="loginForm" type="password" onKeyUp={(e) => {if(e.key === 'Enter') this.manageSubmit()}} onChange={(e) => this.setState({password: e.target.value})} value={this.state.password} />
              </Box>
            </FormGroup>
          </Box>
        </Dialog>
      </>
    );
  }
}
export default App;
