import { Component } from "react";
import "./App.css";

import { Dialog, FormGroup, FormLabel, Input } from '@mui/material';
import { Box } from "@mui/system";

import { login } from './helpers/begin'


class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      username:"",
      password:"",
      needToLogin: true,
      messageToken: "",
      streamToken: "",
      url: "https://chat.cs291.com"
    }
  }

  render() {
    return (
      <>

        <Dialog open={this.state.needToLogin}>
          <Box p={5}>
            <FormGroup>
              <Box pb={2}>
                <FormLabel>
                  {"Username: "}
                </FormLabel>
                <Input onKeyUp={(e) => {if(e.key === 'Enter') login()}} onChange={(e) => this.setState({username: e.target.value})} value={this.state.username}/>
              </Box>
              <Box id="loginForm">
                <FormLabel id="loginForm">
                    {"Password: "}
                  </FormLabel>
                <Input id="loginForm" type="password" onKeyUp={(e) => {if(e.key === 'Enter') login()}} onChange={(e) => this.setState({password: e.target.value})} value={this.state.password} />
              </Box>
            </FormGroup>
          </Box>
        </Dialog>
      </>
    );
  }
}
export default App;
