import React, { Component } from 'react'
import { Paper, List, ListItem, ListItemText } from '@mui/material'


export class MessageList extends Component {
    render() {
        const messages = this.props.events? this.props.events.map( (message) => {
            debugger
            var dateAndTime = new Date(message.created).toLocaleDateString("en-US") + new Date(message.created).toLocaleTimeString("en-US")
            var text = message.user? `${message.user} says: ${message.message} at ${message.created}` : `${message.message} at ${message.created}`
            return( <ListItem key={toString(message.user)+toString(message.created)} disablePadding>
                <ListItemText primary={text} />
            </ListItem>)
        }) : 'no messages'
        return (
            <Paper style={{overflow: 'auto'}}>
                <List>
                {messages}
                </List>
            </Paper>
        )
    }
}

export default MessageList
